import React, { Component, createFactory } from "react";
import { Button, Form, Input, Message } from "semantic-ui-react";
import Layout from "../../components/Layout";
import factory from "../../ethereum/factory";
import web3 from "../../ethereum/web3";
import healthrecord from "../../ethereum/build/healthrecord.json";
import { Router } from "../../routes";

//ipfs requirements
const ipfsClient = require("ipfs-http-client");

const ipfs = ipfsClient({
  host: "ipfs.infura.io",
  port: 5001,
  protocol: "https",
});
const options = [
  { key: "m", text: "Male", value: "male" },
  { key: "f", text: "Female", value: "female" },
  { key: "o", text: "Other", value: "other" },
];
class newRecord extends Component {
  state = {
    name: "",
    bufferReport: null,
    bufferPrescription: null,
    account: null,
    recordhash: null,
    errorMessage: "",
    loading: false,
    age: "",
    gender: "",
  };

  async componentDidMount() {}

  captureFilePrescription = (event) => {
    event.preventDefault();
    const file = event.target.files[0];
    const reader = new window.FileReader();
    reader.readAsArrayBuffer(file);
    reader.onloadend = () => {
      this.setState({ bufferPrescription: Buffer(reader.result) });
      console.log("buffer", this.state.bufferPrescription);
    };
  };

  captureFileReport = (event) => {
    event.preventDefault();
    const file = event.target.files[0];
    const reader = new window.FileReader();
    reader.readAsArrayBuffer(file);
    reader.onloadend = () => {
      this.setState({ bufferReport: Buffer(reader.result) });
      console.log("buffer", this.state.bufferReport);
    };
  };

  onSubmit = async (event) => {
    event.preventDefault();
    console.log(this.state.gender);
  };

  render() {
    return (
      <Layout>
        <Form onSubmit={this.onSubmit} error={!!this.state.errorMessage}>
          <Form.Group widths="equal">
            <Form.Input
              label="name"
              placeholder="name"
              onChange={(event, { value }) => {
                this.setState({ name: value });
              }}
            />
            <Form.Input
              label="age"
              placeholder="age"
              onChange={(e, { value }) => {
                this.setState({ age: value });
              }}
            />
            <Form.Select
              label="gender"
              placeholder="gender"
              options={options}
              onChange={(event, { value }) => {
                this.setState({ gender: value });
              }}
            />
          </Form.Group>
          <Form.Field>
            <label>prescriptions(if any previous prescriptions)</label>
            <Form.Input type="file" onChange={this.captureFilePrescription} />
          </Form.Field>
          <Form.Field>
            <label>reports(if any previous reports)</label>
            <Form.Input type="file" onChange={this.captureFile} />
          </Form.Field>
          <Message error header="Oops!!" content={this.state.errorMessage} />
          <Button loading={this.state.loading} primary type="submit">
            Create
          </Button>
        </Form>
      </Layout>
    );
  }
}

export default newRecord;
