import factory from "../ethereum/factory";
import Layout from "../components/Layout";
import web3 from "../ethereum/web3";
import { Component } from "react";
import { Card, Icon } from "semantic-ui-react";
import { Link } from "../routes";

class doctor extends Component {
  static async getInitialProps() {
    await web3.ethereum.enable();
    const accounts = await web3.eth.getAccounts();
    const docs = await factory.methods.getDoctors().call({ from: accounts[0] });
    return { accounts, docs };
  }
  renderCard(image, name, speciality, address) {
    return (
      <Card>
        <Image src={image} wrapped ui={false} />
        <Card.Content>
          <Card.Header>{name}</Card.Header>
          <Card.Description>{speciality}</Card.Description>
        </Card.Content>
        <Card.Content>
          <Link route={`/doctors/${address}`}>
            <Icon name="user" />
            <a>select</a>
          </Link>
        </Card.Content>
      </Card>
    );
  }
  async renderDoctors() {
    const items = [];
    for (var i = 0; i < this.props.docs.length; i++) {
      const eachDoctor = await factory.methods
        .docs(this.props.docs[i])
        .call({ from: this.props.accounts[0] });
      items.push(
        this.renderCard(
          eachDoctor.imageHash,
          eachDoctor.description,
          eachDoctor.speciality,
          eachDoctor.doc
        )
      );
    }
    return items;
  }
  render() {
    return (
      <Layout>
        <h3>Select your doctors</h3>
        <Card.Group>{this.renderDoctors}</Card.Group>
      </Layout>
    );
  }
}

export default doctor;
