import factory from "../ethereum/factory";
import Layout from "../components/Layout";
import web3 from "../ethereum/web3";
import { Component } from "react";
import { Card, Icon, Image } from "semantic-ui-react";
import { Link } from "../routes";

class doctor extends Component {
  async renderDoctors() {
    const items = [];
    await web3.ethereum.enable();
    const accounts = await web3.eth.getAccounts();
    const docs = await factory.methods.getDoctors().call({ from: accounts[0] });
    console.log(docs);

    items = docs.map(async (address) => {
      const eachDoctor = await factory.methods
        .docs(address)
        .call({ from: accounts[0] });
      return (
        <Card>
          <Image src={eachDoctor.imagehash} wrapped ui={false} />
          <Card.Content>
            <Card.Header>{eachDoctor.description}</Card.Header>
            <Card.Description>{eachDoctor.speciality}</Card.Description>
          </Card.Content>
          <Card.Content>
            <Link route={`/doctors/${address}`}>
              <Icon name="user" />
              <a>select</a>
            </Link>
          </Card.Content>
        </Card>
      );
    });
    return items;
  }
  render() {
    return (
      <Layout>
        <h3>Select your doctors</h3>

        <Card>
          <Image
            src="https://i.pinimg.com/564x/08/61/35/0861350a450c6bdd45a65ce6ce239427.jpg"
            wrapped
            ui={false}
          />
          <Card.Content>
            <Card.Header>asd</Card.Header>
            <Card.Description>asd</Card.Description>
          </Card.Content>
          <Card.Content>
            <Link route="/">
              <a>select</a>
            </Link>
          </Card.Content>
        </Card>
      </Layout>
    );
  }
}

export default doctor;
