import React, { Component } from "react";
import { Button, Form, Icon } from "semantic-ui-react";
import Layout from "../components/Layout";
import factory from "../ethereum/build/RecordFactory.json";

const ipfsClient = require("ipfs-http-client");

const ipfs = ipfsClient({
  host: "ipfs.infura.io",
  port: 5001,
  protocol: "https",
});

class newDoc extends Component {
  state = {
    name: "",
    address: "",
    speciality: "",
    buffer: null,
    loading: false,
    errorMessage: "",
  };
  captureFile = (event) => {
    event.preventDefault();
    const file = event.target.files[0];
    const reader = new window.FileReader();
    reader.readAsArrayBuffer(file);
    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) });
      console.log("buffer", this.state.buffer);
    };
  };
  onSubmit = async (event) => {
    event.preventDefault();
    this.setState({ loading: true, errorMessage: "" });
    try {
      const result = await ipfs.add(this.state.buffer);
      await factory.methods
        .registerDoctor()
        .send({ from: account[0], gas: "1000000" });
    } catch (err) {
      this.setState({ errorMessage: err.message });
    }
    this.setState({ loading: false });
  };
  render() {
    return (
      <Layout>
        <h1>Register Doctor</h1>
        <Form onSubmit={this.onSubmit}>
          <Form.Group widths="equal">
            <Form.Input
              fluid
              label="name"
              placeholder="name"
              onChange={(event) => {
                this.setState({ name: event.target.value });
              }}
            />
            <Form.Input
              fluid
              label="address"
              placeholder="address"
              onChange={(event) => {
                this.setState({ address: event.target.value });
              }}
            />
          </Form.Group>

          <Form.TextArea
            label="description about speciality"
            placeholder="Type here"
            onChange={(event) => {
              this.setState({ speciality: event.target.value });
            }}
          />
          <Form.Input type="file" label="image" onChange={this.captureFile} />
          <Button primary type="submit" loading={this.state.loading}>
            <Icon name="add circle" />
            Register
          </Button>
        </Form>
      </Layout>
    );
  }
}

export default newDoc;
