import React from "react";
import factory from "../ethereum/factory";
import Layout from "../components/Layout";
import web3 from "../ethereum/web3";
import { Component } from "react";
import { Card, Icon, Image } from "semantic-ui-react";
import { Link } from "../routes";

class doctor extends Component {
  state = {
    accounts: [],
    items: [],
  };
  async componentDidMount() {
    const accounts = await web3.eth.getAccounts();
    const docs = await factory.methods.getDoctors().call({ from: accounts[0] });
    const items = docs.map(async (address) => {
      const eachDoctor = await factory.methods
        .docs(address)
        .call({ from: accounts[0] });
      return {
        image: eachDoctor.imageHash,
        header: eachDoctor.description,
        description: eachDoctor.speciality,
      };
    });
    this.setState({ items });
  }
  renderDoctors = () => {
    console.log(this.state.items);
    const items = [
      {
        header: "Project Report - April",
        description:
          "Leverage agile frameworks to provide a robust synopsis for high level overviews.",
        meta: "ROI: 30%",
      },
      {
        header: "Project Report - May",
        description:
          "Bring to the table win-win survival strategies to ensure proactive domination.",
        meta: "ROI: 34%",
      },
      {
        header: "Project Report - June",
        description:
          "Capitalise on low hanging fruit to identify a ballpark value added activity to beta test.",
        meta: "ROI: 27%",
      },
    ];
    return <Card.Group items={items} />;
  };
  render() {
    return (
      <Layout>
        <h3>Select your doctors</h3>
        {this.renderDoctors()}
      </Layout>
    );
  }
}

export default doctor;
